function load_config() {
    if [[ "$2" != "NULL" ]]
        then
            printf "\t<property>\n\t\t<name>$1</name>\n\t\t<value>$2</value>\n\t</property>\n" >> "${HADOOP_CONF_DIR}/$3"
    fi
}

function load_config_with_opt() {
