        then
            printf "\t<property>\n\t\t<name>$1</name>\n\t\t<value>$3</value>\n\t</property>\n" >> "${HADOOP_CONF_DIR}/$5"
    else
        printf "\t<property>\n\t\t<name>$1</name>\n\t\t<value>$4</value>\n\t</property>\n" >> "${HADOOP_CONF_DIR}/$5"
    fi
}

